document.getElementById("submit-btn").addEventListener("click",function(){var n=document.getElementById("uploadPercentage"),t=document.getElementById("uploadedFile");t.addEventListener("change",function(){var i=this.files[0],r=i.size,t=new XMLHttpRequest;t.open("POST","YourUploadActionUrl",!0);t.upload.onprogress=function(t){if(t.lengthComputable){var i=t.loaded/r*100;n.innerText=i.toFixed(2)+"%"}};t.send(new FormData(document.getElementById("upload-form")))})});